"use client";

import { motion } from "framer-motion";

export default function Categories() {
  const items = [
    { title: "Fashion", image: "data:image/jpeg;base64," },
    { title: "Gadgets", image: "data:image/jpeg;base64," },
    { title: "Home Essentials", image: "data:image/jpeg;base64," },
  ];

  return (
    <section className="py-24 px-6 sm:px-16">
      <h2 className="text-4xl font-semibold text-center mb-12">
        Shop by Category
      </h2>

      <div className="grid grid-cols-1 sm:grid-cols-3 gap-8">
        {items.map((item, idx) => (
          <motion.div
            key={item.title}
            initial={{ opacity: 0, y: 40 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: idx * 0.15 }}
            viewport={{ once: true }}
            className="relative h-64 rounded-xl overflow-hidden group cursor-pointer shadow-lg"
          >
            <img
              src={item.image}
              className="w-full h-full object-cover group-hover:scale-110 transition duration-500"
            />

            <div className="absolute inset-0 bg-black/30 flex items-center justify-center">
              <p className="text-white text-2xl font-semibold">
                {item.title}
              </p>
            </div>
          </motion.div>
        ))}
      </div>
    </section>
  );
}
